import React, { Component } from 'react'
import axios from "axios";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';




export default class AnimalsComponent extends Component {

    state = {
        name:'',
        animals: []
    }
    async componentDidMount() {
        this.getAnimals();
    }

    getAnimals = async () => {
        const res = await axios.get('http://localhost:4000/api/animals');
        this.setState({
            animals: res.data
        });

    }

    onChangeAnimalName
    render() {
        return(
            <>
                <div className="row">
                    <div className="card card-body">
                        <h3>Create New Animal</h3>
                        <form action="">
                            <div className="form-group">
                                <input type="text" className="form-control" onChange={this.onChangeAnimalName} />                            </div>
                        </form>
                    </div>
                    <Table striped bordered hover>
                      <thead>
                        <tr>
                          <th>#</th>
                          <th>Name</th>
                          <th>Creation date</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {
                            this.state.animals.map(animal => (
                                <tr>
                                      <td>{animal._id}</td>
                                      <td>{animal.name}</td>
                                      <td>{animal.date}</td>
                                      <td>
                                          <Button variant="outline-warning">Editar</Button>{' '}
                                          <Button variant="outline-danger">Eliminar</Button>{' '}
                                      </td>
                                </tr>
                                    ))
                                }
                      </tbody>
                </Table>
                </div>
             </>
        )

    }
}
